function get_formatted_date(local_date) {
	const warsaw_formatter = new Intl.DateTimeFormat('en-US', {
		timeZone: 'Europe/Warsaw',
		year: 'numeric',
		month: '2-digit',
		day: '2-digit',
		hour: '2-digit',
		minute: '2-digit',
		second: '2-digit',
		hour12: false
	});

	const [
		{ value: month },,
		{ value: day },,
		{ value: year },,
		{ value: hour },,
		{ value: minute },,
		{ value: second }
	] = warsaw_formatter.formatToParts(local_date);

	return new Date(`${year}-${month}-${day}T${hour}:${minute}:${second}.000Z`);
}
function transform_to_wo_format(reservation) {
    const reservation_details = reservation.details
	const [start, end] = reservation_details.time.split(' - ');

	const [day, month, year] = reservation_details.date.split('-').map(Number);
	const [startHour, startMinute] = start.split(':').map(Number);
	const [endHour, endMinute] = end.split(':').map(Number);

	const local_start_date = new Date(year, month - 1, day, startHour, startMinute);
	const local_end_date = new Date(year, month - 1, day, endHour, endMinute);

	const start_date_time = get_formatted_date(local_start_date);
	const end_date_time = get_formatted_date(local_end_date);

	return {
		// id: parseInt(reservation_details.reservation_number.replace(/\D/g, '')),
        type: '1',
		workspace: 13096,
        time: 30,
		licencePlate: reservation_details.registration_number || '',
		firstname: reservation_details.client_name.split(" ")[0] || ' ',
		lastname: reservation_details.client_name.split(" ")[1] || ' ',
		phone: reservation_details.phone.replace(/\D/g, '') || '',
		email: reservation_details.email || '',
        category: 1,
		notes: reservation.reservation_url || '',
		fromDate: start_date_time.toISOString(),
		toDate: end_date_time.toISOString(),
        reservationType: "1",
	};
}
const inputData = $input.all().map(i => i.json);

if(inputData[0].data.length === 0){
  return [];
}

const transformed_data = [];

for (const reservation of inputData[0].data) {
  transformed_data.push(transform_to_wo_format(reservation));
}

return transformed_data.map(item => ({ json: item }));

